USE `news01`;

CREATE TABLE IF NOT EXISTS `news` (
  `id` mediumint(6) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `tag` varchar(5) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '標籤',
  `title` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '標題',
  `author` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '作者',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '文章內容',
  `create_time` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT '建檔時間',
  `release_state` mediumint(1) NOT NULL DEFAULT 0 COMMENT '公布狀態 0=上架(預設) 1=下架 2=刪除',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `tag` (`tag`) USING BTREE,
  KEY `title` (`title`) USING BTREE,
  KEY `author` (`author`) USING BTREE,
  KEY `create_time` (`create_time`) USING BTREE,
  KEY `release_state` (`release_state`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=latin1;


;



CREATE TABLE IF NOT EXISTS `account` (
  `id` smallint(5) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `username` varchar(15) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '帳號',
  `password` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '密碼',
  `permission` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'USER' COMMENT '權限(ADMIN.USER(預設) )',
  `create_time` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT '創建時間',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `username` (`username`) USING BTREE,
  KEY `password` (`password`) USING BTREE,
  KEY `permission` (`permission`) USING BTREE,
  KEY `create_time` (`create_time`) USING BTREE
) ;


